app.factory('Session', ['Auth', '$state', 'Restangular', function(Auth, $state, Restangular){
  var authenticated = { status: Auth.isAuthenticated() };
  var currentUser = { user: Auth.currentUser() };

  function signIn(credentials) {
    var config = {
        headers: {
            'X-HTTP-Method-Override': 'POST'
        }
    };

    Auth.login(credentials, config).then(function(user){
      console.log('Signed In');
      currentUser.user = user;
      authenticated.status = true;
      $state.go('home');
    }, function(error){
      console.log('Sign In Failed:', error);
      authenticated.status = false;
    });

  }

  // function signInWithGithub() {
  //   console.log('sign in with github');
  //   Restangular.all('users').customGET('auth/github', {}, { 'Access-Control-Allow-Origin': 'http://localhost:3000' });
  // }

  function signOut () {
    var config = {
        headers: {
            'X-HTTP-Method-Override': 'DELETE'
        }
    };

    Auth.logout(config).then(function(oldUser){
      console.log('Signed Out' + oldUser);
      currentUser.user = null;
      authenticated.status = false;
      $state.go('home');
    }, function(error){
      console.log('Sign Out Failed:', error);
    });
  }

  return {
    authenticated: authenticated,
    currentUser: currentUser,
    signIn: signIn,
    signOut: signOut
  };
}]);
app.factory('SignUp', ['Auth', '$state', 'Restangular', function(Auth, $state, Restangular){

  var credentials = {};

  function register(credentials, profile){
    var config = {
        headers: {
            'X-HTTP-Method-Override': 'POST'
        }
    };

    Auth.register(credentials, config).then(function(registeredUser) {
      var profileInfo = {};
      // Send information to backend to create user profile
      profileInfo.user_id = registeredUser.id;
      profileInfo.availability = profile.availability;
      profileInfo.profile_languages_attributes = processProfileInput(profile.profile_languages);
      Restangular.all('profiles').customPUT({profile: profileInfo}).then(function(profile){
        console.log('updated profile: ', profile);
      }, function(error) {
        console.log('cannot update profile');
      });

    }, function(error) {
      alert('Registration failed');
    });
  }

  function processProfileInput(profileInput) {
    var count = 1;
    var result = {};
    for(var key in profileInput) {
      result[count] = {language_id: key, difficulty_id: profileInput[key]};
      count++;
    }
    return result;
  }

  return {
    register: register,
    credentials: credentials
  };

}]);
